import React, { useState, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { Card } from 'react-bootstrap';
import './hero.scss'; // Import the CSS file

export default function HomePage() {
  const [searchQuery, setSearchQuery] = useState('');
  const controls = useAnimation();

  useEffect(() => {
    controls.start({
      y: [0, -10, 0],
      transition: { repeat: Infinity, duration: 2 }
    });
  }, [controls]);

  const handleSearch = (e) => {
    e.preventDefault();
    console.log('Search query:', searchQuery);
  };

  return (
    <div className="homepage-container">
      <main>
        {/* Hero Section */}
        <section className="hero-section">
          <motion.div
            initial={{ opacity: 0.1, y: 50 }}
            animate={{ opacity: 2, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center"
          >
            <h2 className="hero-title">Unlock Your Potential with Expert Mentorship</h2>
            <p className="hero-subtitle">Connect with industry leaders, accelerate your career growth...</p>
            <div className="flex">
              <button className="cta-button">Get Started</button>
              <form onSubmit={handleSearch} className="search-form">
                <input
                  type="text"
                  placeholder="Search for mentors or topics..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
                <button type="submit" className="search-button">Search</button>
              </form>
            </div>
          </motion.div>
        </section>

        {/* Other sections go here */}
      </main>
    </div>
  );
}
