import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Form.scss'; // Assuming you're using the same styles
import Navbar from './../Navbar/Navbar';
import useLogin from './../../hooks/useLogin'; // A custom hook for handling login
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const LoginPage = () => {
  const { login, isLoading, error, success } = useLogin(); // useLogin hook for login logic
  const [FormData, setFormData] = useState({
    username: '',
    password: ''
  });

  const handleInput = (e) => {
    const { name, value } = e.target;
    setFormData({ ...FormData, [name]: value });
  };

  const LoginUser = async (e) => {
    e.preventDefault();
    const { username, password } = FormData;
    await login(username, password);
  };

  const toast_property = () => {
    const obj = {
      position: 'top-right',
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      theme: 'colored'
    };
    return obj;
  };

  return (
    <>
      <Navbar isLogin="false" />
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />

      {error && toast.error(error, toast_property())}
      {success && toast.success(success, toast_property())}

      <div className="form-body">
        <div className="form-box">
          <div className="form-header">
            <h3>Login</h3>
          </div>
          <div className="form-inner-body">
            <form method="post" onSubmit={LoginUser}>
              <div className="input-group">
                <input
                  value={FormData.username}
                  onChange={handleInput}
                  type="text"
                  placeholder="Username"
                  name="username"
                  id="username"
                />
                <label htmlFor="username">Username</label>
              </div>
              <div className="input-group">
                <input
                  value={FormData.password}
                  onChange={handleInput}
                  type="password"
                  placeholder="Password"
                  name="password"
                  id="password"
                />
                <label htmlFor="password">Password</label>
              </div>
              <div className="input-group">
                <button className="btn" type="submit">
                  Log In
                </button>
              </div>
            </form>
            <p>
              Don't have an account? <Link to={'/signup'}>Sign up</Link>
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginPage;
