import { useState } from "react";
import useAuthContext from './useAuthContext'
const useSignup = () => {
    const [error, setError] = useState(null)
    const [success, setsuccess] = useState(null)
    const [isLoading, setIsLoading] = useState(null)
    const {dispatch} = useAuthContext()

    const signup = async (name,username,email,password,role,profile)=>{
        setIsLoading(true)
        setError(null);
        console.log(role);
        if(role=='mentee'){const response = await fetch('/api/mentee/signup',{
            method:'POST',
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify({name,username,email,password,role,profile})
        })}
        else{
            const response = await fetch('/api/mentor/signup',{
                method:'POST',
                headers:{"Content-Type":"application/json"},
                body:JSON.stringify({name,username,email,password,role,profile})
            })
        }
        const json = await response.json();
        if(!response.ok){
            setIsLoading(false);
            setError(json.error)
        }
        if(response.ok){

            localStorage.setItem('user',JSON.stringify(json))
            setsuccess('User has been created successfully')
            dispatch({
                type:'LOGIN',
                payload:json
            })

            setIsLoading(false)
        }
    }
    return {signup,isLoading,error,success}
}

export default useSignup
