import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext'; // Adjust the path as needed
import './Form.scss'; // Ensure this file contains necessary styles

const MySessions = () => {
    const { user } = useContext(AuthContext);
    const [sessions, setSessions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        if (user) {
            fetchSessions();
        }
    }, [user]);

    const fetchSessions = async () => {
        try {
            const response = await fetch('/api/mysessions?email=' + user.email, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${user.token}` // Include token if required
                }
            });

            if (response.ok) {
                const data = await response.json();
                setSessions(data.sessions);
                setLoading(false);
            } else {
                const error = await response.json();
                setError(error.message);
                setLoading(false);
            }
        } catch (err) {
            console.error('Error:', err);
            setError('An error occurred while fetching sessions.');
            setLoading(false);
        }
    };

    if (loading) return <div className="form-body"><div className="loading">Loading sessions...</div></div>;
    if (error) return <div className="form-body"><div className="error">{error}</div></div>;

    return (
        <div className="form-body">
            <div className="form-box">
                <div className="form-header">
                    <h3>My Sessions</h3>
                </div>
                <div className="form-inner-body">
                    {sessions.length === 0 ? (
                        <p>No sessions found.</p>
                    ) : (
                        <table className="sessions-table">
                            <thead>
                                <tr>
                                    <th>Date</th>
                                    <th>Start Time</th>
                                    <th>End Time</th>
                                    <th>Topic</th>
                                    <th>Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {sessions.map((session) => (
                                    <tr key={session.sessionId}>
                                        <td>{new Date(session.date).toLocaleDateString()}</td>
                                        <td>{session.startTime}</td>
                                        <td>{session.endTime}</td>
                                        <td>{session.topic}</td>
                                        <td>{session.status}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
        </div>
    );
};

export default MySessions;
